MODDIR=${0%/*}

zeshia() {
	if [ -f $2 ]; then
		chmod 644 $2 >/dev/null 2>&1
		echo $1 >$2 2>/dev/null
		chmod 444 $2 >/dev/null 2>&1
	fi
}
# Logging 
sendlog() {
    local LEVEL="$1"
    local MODULE="$2"
    local MESSAGE="$3"
    local TIMESTAMP
    TIMESTAMP="$(date '+%Y-%m-%d %H:%M:%S.%3N')"
    echo "$TIMESTAMP $LEVEL $MODULE: $MESSAGE" >> /data/adb/.config/encore/encore.log
}
